import React, { useState, useEffect } from "react";

const Product = () => {
  const [data, setData] = useState([]);
  const [filetr, setFilter] = useState(data);
  const [loading, setLoading] = useState(false);
  let componentMounted = true;
  useEffect(() => {
    const getProduct = async () => {
      setLoading(true);
      const responce = await fetch("http://fakestoreapi.com/products");
      if (componentMounted) {
        setData(await responce.clone().json());
        setFilter(await responce.json());
        setLoading(false);
        console.log(filetr);
      }
      return () => {
        componentMounted = false;
      };
    };
    getProduct();
  }, []);
  const Loading = () => {
    return(
        <>
        Loading....
        </>
    )
  };
  const ShowProduct = () => {
    return(
        <>
        <div className="buttons d-flex justify-content-center mp-5 pd-5">
            <button className="btn btn-outline-dark me-2">All</button>
            <button className="btn btn-outline-dark me-2">Men's Clothing</button>
            <button className="btn btn-outline-dark me-2">Women's Clothing</button>
            <button className="btn btn-outline-dark me-2">Jewelery</button>
            <button className="btn btn-outline-dark me-2">Electronic</button>
        </div>
        {filetr.map((product)=>{
            return(
                <>
                <div className="col-md-3">

                <div class="card" >
  <img src={product.image} class="card-img-top" alt={product}/>
  <div class="card-body">
    <h5 class="card-title">Card title</h5>
    <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
    <a href="#" class="btn btn-primary">Go somewhere</a>
  </div>
</div>
                </div>
                </>
            )
        })}
        </>
    )
  };
  return (
    <>
      <div className="container my-5 py-5">
        <div className="row">
          <div className="col-12 mb-5">
            <h1 className="display-6 fw-bolder text-center">LATEST PRODUCT</h1>
            <hr />
          </div>
        </div>
        <div className="row justify-content-center">
          {loading ? <Loading /> : <ShowProduct />}
        </div>
      </div>
    </>
  );
};

export default Product;
